import React, { useState } from 'react';
import { AppBar, Button, Drawer, List, ListItem, IconButton, Typography, Toolbar, useMediaQuery } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

import { MobileMenu } from './MobileMenu';
import { DesktopMenu } from './DesktopMenu';

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  title: {
    flexGrow: 1,
  },
}));

export const Header = () => {
  const classes = useStyles();
  const isUpToSm = useMediaQuery(theme => theme.breakpoints.up('sm'));
  const [ isOpenDrawer, setDrawer ] = useState(false);

  const renderDrawerList = () => (
    <div
      role="presentation"
      onClick={toggleDrawer()}
      onKeyDown={toggleDrawer()}
    >
      <List>
        <ListItem>
          <Button color="inherit">Login</Button>
        </ListItem>
      </List>  
    </div>
  );

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setDrawer(!isOpenDrawer);
  };
  return (
    <AppBar>
      <Toolbar>  
        <Typography variant="h6" className={classes.title}>
          News
        </Typography>

        {isUpToSm && DesktopMenu}

        {!isUpToSm && MobileMenu}
      </Toolbar>
    </AppBar>
  );
};
